mapper.select:
SELECT

FROM sys_office a

WHERE a.id = #{id}

SELECT
	a.*
FROM sys_office a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name = #{name}


		AND a.code = #{code}

SELECT t.* FROM sys_office t WHERE t.del_flag = #{DEL_FLAG_NORMAL}
start with t.id = #{id} CONNECT by PRIOR t.parent_id = t.id
UNION SELECT t.* FROM sys_office t WHERE t.del_flag = #{DEL_FLAG_NORMAL}
start with t.id = #{id} CONNECT by PRIOR t.id = t.parent_id

SELECT * FROM ( SELECT t.* FROM sys_office t WHERE t.del_flag = #{parentOffice.DEL_FLAG_NORMAL}
start with t.id = #{parentOffice.id} CONNECT by PRIOR t.id = t.parent_id
) WHERE 1 = 1
AND id = #{childOffice.id}

mapper.update:
UPDATE sys_office SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}
